</figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":164,\"width\":768,\"height\":576,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-large is-resized\">
